    private void rButHombreActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_rButHombreActionPerformed
        if(rButHombre.isSelected()== true)
            rButMujer.setEnabled(false); // TODO add your handling code here:
        if(rButHombre.isSelected() == false){
            rButMujer.setEnabled(true);
        }
    }//GEN-LAST:event_rButHombreActionPerformed

    private void rButMujerActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_rButMujerActionPerformed
        if(rButMujer.isSelected()== true)
            rButHombre.setEnabled(false); // TODO add your handling code here:
        if(rButMujer.isSelected() == false){
            rButHombre.setEnabled(true);
        }
    }//GEN-LAST:event_rButMujerActionPerformed

